import javax.swing.*;
import java.awt.*;

import java.io.ByteArrayInputStream;
import javax.imageio.ImageIO;
import java.awt.image.BufferedImage;
import java.util.Base64;
import java.util.Base64.Decoder;

public class ProxyImageIcon extends ImageIcon {

	private String name;
	private ImageIcon image;
	private final int DIMENSION = 16;
	static int loadedIcons = 0;

	public ProxyImageIcon(String name) {
		this.name = name;
		//image = GetImageIcon(name);
	}

	public Image getImage() {
		return GetImageIcon(name).getImage();
	}

	public int getIconWidth() {
		return DIMENSION;
	}

	public int getIconHeight() {
		return DIMENSION;
	}

	public void paintIcon(Component c, Graphics g, int x, int y) {
		if (image == null) {
			image = GetImageIcon(name);
			System.out.println("Number of loaded icons: " + ++loadedIcons);
		}
		image.paintIcon(c, g, x, y);
	}

	public ImageIcon GetImageIcon(String textId) {
		
		String b64string = null;
		
		switch (textId)
		{
			// https://www.base64-image.de/
			case "iconInfo.gif": b64string = ""; break;
			case "truetype.gif": b64string = ""; break;
			case "bubble_icon.gif": b64string = ""; break;
			case "close-icon.gif": b64string = ""; break;
			case "exit-24-000000.png": b64string = ""; break;
			case "c02228162.jpg": b64string = ""; break;
			case "network_icon.jpg": b64string = ""; break;
			case "icon_disconnect_agent.bmp.png": b64string = ""; break;
			case "netico.gif": b64string = ""; break;
			case "debug.png": b64string = ""; break;
			case "WordWrap.png": b64string = "iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAABhSURBVDhPY2AYFCA+Pv4/DIMchMzHJ049x2OzEZcrqGcrsknY/ElMWFDPNfjCANklEBv//0dgot3wTx5TKcggggCk8W8zxEZ0QNAAkGZk52JjU+QCgpqRFWALA5IMoKFiALpnfwxKe4MuAAAAAElFTkSuQmCC"; break;
			case "ui-status-bar.png": b64string = ""; break;
			case "nav_zoomout.png": b64string = ""; break;
			case "nav_zoomin.png": b64string = ""; break;
			case "botao-refazer.png": b64string = ""; break;
			case "back_undo.png": b64string = ""; break;
			case "edit_select_all.png": b64string = ""; break;
			case "editor_panel_tab_icon.gif": b64string = ""; break;
			case "edit_find_replace.png": b64string = ""; break;
			case "1398640998.png": b64string = ""; break;
			case "mai1444425541180_lowres_en-us.png": b64string = ""; break;
			case "icon_copy_n.png": b64string = ""; break;
			case "save.png": b64string = ""; break;
			case "Document New-01.png": b64string = ""; break;
			case "Open_file.png": b64string = ""; break;
			case "saveas_icon.gif": b64string = ""; break;
			case "file_edit.gif": b64string = ""; break;
			case "spellcheck.png": b64string = ""; break;
			case "License.png": b64string = ""; break;
			//case "": b64string = ""; break;
			
			default: System.err.println("Base64 data for image '" + textId + "' not found.");
		}
		
		try
		{
			byte[] decodedBytes = Base64.getDecoder().decode(b64string); 
			BufferedImage source = ImageIO.read(new ByteArrayInputStream(decodedBytes));
			Image image = source.getScaledInstance(DIMENSION, DIMENSION, Image.SCALE_SMOOTH);
			return new ImageIcon(image);
		} catch (Exception e) {
			System.err.println("Base64 decoding failed and/or image couldn't be created: " + e.getMessage());
			return null;
		}
		
		/*
			yourImage.getScaledInstance(newWidth, newHeight, Image.SCALE_DEFAULT);
			https://stackoverflow.com/questions/5895829/resizing-image-in-java
		*/
	}
}
